draw(a);
draw(shift((15,8.7))*a);
draw(shift((15,-8.7))*a);
draw(shift((30,17.3))*a);
draw(shift((30,0))*a);
draw(shift((30,-17.3))*a);
draw(shift((45,26))*a);
draw(shift((45,8.7))*a);
draw(shift((45,-8.7))*a);
